#!/usr/bin/ruby
c, word, count, actWord = [], proc {|s| s[rand(s.size)] }.call(["letmein", "hello world"]), proc { |w,c| c.size - w.split(//).uniq.join.count(c.join) }, proc { |w,c| w.gsub(/[^#{c.join.empty? ? "." : c.join}\s]/, '_') }
print(actWord.call(word, c),"  (", count.call(word, c), " bad tries)\n", (actWord.call(word, c) == word ? "geschafft" : "")) while (count.call(word, c) < 10 && actWord.call(word, (c=(c+((gets or break).strip[0] or next).split(//)))) != word)